CREATE TABLE IF NOT EXISTS `indiamp3` (
  `no` int(16) NOT NULL AUTO_INCREMENT,
  `category` varchar(64) NOT NULL,
  `albumname` varchar(64) NOT NULL,
  `bitrate` varchar(16) NOT NULL,
  `cover` varchar(512) NOT NULL,
  `songname` varchar(128) NOT NULL,
  `singer` varchar(64) NOT NULL,
  `url_h` varchar(512) NOT NULL,
  `url_l` varchar(512) NOT NULL,
  `download` char(1) NOT NULL,
  PRIMARY KEY (`no`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

